import React from 'react'
import headerLogo from '../img/logo.png'

export default class Header extends React.Component {
  constructor() {
    super()
    this.handlerLogin = this.handlerLogin.bind(this)
  }

  handlerLogin(evt) {
    evt.preventDefault()
    this.props.setFormType(evt.target.getAttribute('data-type'))
  }

  getLogin() {
       
    async function fetchGetLogin() {

      console.log('fetch')
      const res = await fetch('/api/auth', { 
          headers: {
            'Content-Type':'application/json',
            'Authorization': localStorage.getItem('token')
          },
        }
      )
      const result = res.json()
      if (res.ok) {
        //take data
        return result
      } // нет проверки на ошибки при get запросе!!!!
    }

    return fetchGetLogin()

        // this.setState( () => {
        //   for (let item of result) {
        //     item.id = item.id.toString()
        //   }
        //   return {
        //     todos: result,
        //     isToken: true
        //   }
        // })
        //   } else {
        //     if (res.status === 401) {
        //       this.props.changeForm('AuthForm')
        //     } else {
        //       console.log(result.msg)
        //     }
        //   }
        // }
  }

  render() {

    return (
    <header className='header'>

      <div className='header__wrap'>

        <div className='logo'>
          <a className='logo__link' href='/'>
            <img alt='logo' src={headerLogo} />
          </a> 
        </div>
      
        <div className='nav'>
          <ul className='nav__list'>
            <li className='nav__elem'>
              <a className='nav__link' href='/'>Главная</a>
            </li>
            <li className='nav__elem'>
              <a className='nav__link' href='/'>До 200 €</a>
            </li>
            <li className='nav__elem'>
              <a className='nav__link' href='/'>До 1000 €</a>
            </li>
            <li className='nav__elem'>
              <a className='nav__link' href='/'>Личный кабинет</a>
            </li>
          </ul>
        </div>

        <div className='login'>
          <a className='login__enter lg-style' href='/' onClick={this.handlerLogin} data-type='auth'>
            {localStorage.length ? this.getLogin() : 'Вход'}
          </a>
          <span> / </span>
          <a className='login__reg lg-style' href='/' onClick={this.handlerLogin} data-type='register'>
            {localStorage.length ? 'Выход' : 'Регистрация'}
          </a>
        </div>

      </div>
    </header>
    )
  }
}
