import React from 'react'
import headerLogo from '../img/logo.png'

export default class Header extends React.Component {
  constructor() {
    super()
    this.state = {

    }

    this.handlerLogin = this.handlerLogin.bind(this)
    this.handlerExit = this.handlerExit.bind(this)
    this.handlerNav = this.handlerNav.bind(this)
  }

  handlerLogin(evt) {
    evt.preventDefault()
    this.props.setFormType(evt.target.getAttribute('data-type'))
  }

  handlerAccount(evt) {
    evt.preventDefault()
    alert('Личный кабинет находится в разработке...')
  }

  handlerExit(evt) {
    evt.preventDefault()
    localStorage.removeItem('token')
    this.props.delLogin()
    this.props.setMainType('home')
    this.props.setOrderType()
  }

  handlerNav(evt) {
    evt.preventDefault()
    // console.log(evt.target.getAttribute('href'))
    switch(evt.target.getAttribute('href')) {
      case '200': {
        if (localStorage.length) this.props.setOrderType('200')
        else {
          this.props.setDesiredOrder('200')
          this.props.setFormType('auth')
        }
        break
      }
      case '1000': {
        if (localStorage.length) this.props.setOrderType('1000')
        else {
          this.props.setDesiredOrder('1000')
          this.props.setFormType('auth')
        }
        break
      }
      case 'account': {
        alert('Личный кабинет находится в разработке...')
        break
      }
      default: return
    }
  }

  ////////////////////////////

  getLinksStyle(evt) {
    const tempClass = evt.target.classList[1].slice(3)
    if (tempClass === this.props.selectedPage) return
    else {
      const style = evt.target.style
      if (style.textDecoration === 'none') {
        style.textDecoration = 'underline'
        style.color = '#00A0E3' 
      } else {
        style.textDecoration = 'none'
        style.color = 'inherit'
      }
    }
  }

  linksOnFocus(evt) {
    console.log('Focus')
    this.getLinksStyle(evt)
    // const style = evt.target.style
    // style.textDecoration = 'underline'
    // style.color = '#00A0E3'
  }

  linksOnBlur(evt) {
    console.log('Blur')
    this.getLinksStyle(evt)
    // const style = evt.target.style
    // style.textDecoration = 'none'
    // style.color = 'inherit'
  }

  linksOnMouseEnter(evt) {
    console.log('MouseEnter')
    this.getLinksStyle(evt)
    // this.show.call(this)

    // console.log(evt.target.classList[1].slice(3))
    // const style = evt.target.style
    // style.textDecoration = 'underline'
    // style.color = '#00A0E3'
  }

  linksOnMouseLeave(evt) {
    console.log('MouseLeave')
    this.getLinksStyle(evt)
    // const style = evt.target.style
    // style.textDecoration = 'none'
    // style.color = 'inherit'
  }

  //////////////////
  bar() {
    console.log('ok')
  }

  foo(evt) {
    evt.preventDefault()
    console.log('ok')
    this.bar.call(this)
  }

  render() {
    console.log(this.props.selectedPage)

    const logRegBtns = (
      <div className='login'>
        <a className='login__enter lg-style' href='/' onClick={this.handlerLogin} data-type='auth'>
          Вход
        </a>
        <span> / </span>
        <a className='login__reg lg-style' href='/' onClick={this.handlerLogin} data-type='register'>
          Регистрация
        </a>
      </div>
    )

    const accExitBtns = (
      <div className='login'>
        <a className='login__account lg-style' href='/' onClick={this.handlerAccount} data-type='auth'>
          {this.props.login}
        </a>
        <span> / </span>
        <a className='login-exit lg-style' href='/' onClick={this.handlerExit} data-type='register'>
          Выход
        </a>
      </div>
    )

    return (
    <header className='header'>

      <div className='header__wrap'>

        <div className='logo'>
          <a className='logo__link' href='/' onClick={this.foo}>
            <img alt='logo' src={headerLogo} />
          </a> 
        </div>
      
        <div className='nav'>
          <ul className='nav__list'>
            <li className='nav__elem'>
              <a 
                className='nav__link js-home' 
                href='/' 
                onFocus={this.linksOnFocus} 
                onBlur={this.linksOnBlur}
                onMouseEnter={this.linksOnMouseEnter}
                onMouseLeave={this.linksOnMouseLeave}
              >
                Главная
              </a>
            </li>
            <li className='nav__elem js-form200'>
              <a 
                className='nav__link' 
                href='200' 
                onClick={this.handlerNav} 
                onFocus={this.linksOnFocus} 
                onBlur={this.linksOnBlur}
                onMouseEnter={this.linksOnMouseEnter}
                onMouseLeave={this.linksOnMouseLeave}
              >
                До 200 €
              </a>
            </li>
            <li className='nav__elem'>
              <a 
                className='nav__link js-form1000' 
                href='1000' 
                onClick={this.handlerNav} 
                onFocus={this.linksOnFocus} 
                onBlur={this.linksOnBlur}
                onMouseEnter={this.linksOnMouseEnter}
                onMouseLeave={this.linksOnMouseLeave}
              >
                До 1000 €
              </a>
            </li>
            <li className='nav__elem'>
              <a 
                className='nav__link js-account' 
                href='account' 
                onClick={this.handlerNav} 
                onFocus={this.linksOnFocus} 
                onBlur={this.linksOnBlur}
                onMouseEnter={this.linksOnMouseEnter}
                onMouseLeave={this.linksOnMouseLeave}
              >
                Личный кабинет
              </a>
            </li>
          </ul>
        </div>
        {localStorage.length ? accExitBtns : logRegBtns}

      </div>
    </header>
    )
  }
}
