import React from 'react'
import loginIcon from '../img/icons/login.svg'

export default class Auth extends React.Component {
  constructor() {
    super()
    this.state={
      //login
      // formType: 'login'
      formType: 'register'
      // formType: 'recovery'
    }
  }

  getDynamicData() {
    //title
    const data = []
    switch(this.state.formType) {
      case 'login': {
        data.push('Вход')
        data.push(null)
        data.push(
          <div className='auth__links'>
            <a className='link__register' href='/'>
              Регистрация
            </a>
            <a className='link__recovery' href='/'>
              Забыли пароль?
            </a>
          </div>
        )
      }
      case 'register': {
        data.push('Регистрация')
        data.push(
          <div className='auth__password'>
            <input className='login__input' type='password' placeholder='Повторите пароль'/>
          </div>
        )
        data.push(
          <div className='auth__links' style>
            <a className='link__login' href='/'>
              У меня уже есть аккаунт!
            </a>
          </div>
        )
      }
      case 'recovery': {
        data.push('Восстановление пароля')
        data.push(null)
      }
    }
    return data
  }

  render() {
    const state = this.state

    const [title, repeatInput, links] = this.getDynamicData()

    // const repeatInput = state.formType === 'register' ? 
    // <div className='auth__password'>
    //   <input className='login__input' type='password' placeholder='Повторите пароль'/>
    // </div> : null

    const btnText = state.formType === 'register' ? 'Зарегистрироваться'
    : state.formType === 'recovery' ? 'Восстановить пароль'
    : 'Войти'

    return (
      <div className='auth'>
        <div className='auth__container'>
          <h1 className='auth__title'>
            {title}
          </h1>
          <div className='auth__wrap'>
            <div className='auth__login'>
              <input className='login__input' type='email' placeholder='Адрес эл.почты'/>
            </div>
            <div className='auth__password'>
              <input className='login__input' type='password' placeholder='Пароль'/>
            </div>
            {repeatInput}
            <button className='auth__btn-auth'>
                {btnText}
            </button>
            {links}
            {/* <div className='auth__links'>
              <a className='link__register' href='/'>
                Регистрация
              </a>
              <a className='link__recovery' href='/'>
                Забыли пароль?
              </a>
            </div> */}
          </div>
        </div>
      </div>
    )
  }
}





