import React from 'react'
import headerLogo from '../img/logo.png'

export default class Header extends React.Component {
  constructor() {
    super()
    this.state = {

    }

    this.handlerLogin = this.handlerLogin.bind(this)
    this.handlerExit = this.handlerExit.bind(this)
  }

  handlerLogin(evt) {
    evt.preventDefault()
    this.props.setFormType(evt.target.getAttribute('data-type'))
  }

  handlerAccount(evt) {
    evt.preventDefault()
    alert('Личный кабинет находится в разработке...')
  }

  handlerExit(evt) {
    evt.preventDefault()
    localStorage.removeItem('token')
    this.props.delLogin()
    this.props.setMainType('home')
  }

  handlerNav(evt) {
    evt.preventDefault()
    console.log(evt.target.getAttribute('href'))
    switch(evt.target.getAttribute) {
      case '200': {

      }
      case '1000': {

      }
      case 'account': {

      }
      default: alert('Личный кабинет находится в разработке...')
    }
  }

  render() {

    const logRegBtns = (
      <div className='login'>
        <a className='login__enter lg-style' href='/' onClick={this.handlerLogin} data-type='auth'>
          Вход
        </a>
        <span> / </span>
        <a className='login__reg lg-style' href='/' onClick={this.handlerLogin} data-type='register'>
          Регистрация
        </a>
      </div>
    )

    const accExitBtns = (
      <div className='login'>
        <a className='login__account lg-style' href='/' onClick={this.handlerAccount} data-type='auth'>
          {this.props.login}
        </a>
        <span> / </span>
        <a className='login-exit lg-style' href='/' onClick={this.handlerExit} data-type='register'>
          Выход
        </a>
      </div>
    )

    return (
    <header className='header'>

      <div className='header__wrap'>

        <div className='logo'>
          <a className='logo__link' href='/'>
            <img alt='logo' src={headerLogo} />
          </a> 
        </div>
      
        <div className='nav'>
          <ul className='nav__list'>
            <li className='nav__elem'>
              <a className='nav__link' href='/'>Главная</a>
            </li>
            <li className='nav__elem'>
              <a className='nav__link' href='200' onClick={this.handlerNav}>До 200 €</a>
            </li>
            <li className='nav__elem'>
              <a className='nav__link' href='1000' onClick={this.handlerNav}>До 1000 €</a>
            </li>
            <li className='nav__elem'>
              <a className='nav__link' href='account' onClick={this.handlerNav}>Личный кабинет</a>
            </li>
          </ul>
        </div>
        {localStorage.length ? accExitBtns : logRegBtns}

      </div>
    </header>
    )
  }
}
